    dplyr::pull(home_score)

  away_scores <- pbp_selected_diff %>%
    dplyr::filter(away_team == team_abbr) %>%
    dplyr::pull(away_score)

  t.test(home_scores, away_scores,
         conf.level = 0.95,
         alternative = "two.sided")
}

